const http = require\(''http''\);
http.get\(''http://localhost:9222/json'', \(res\) => {
  let data = '''';
  res.on\(''data'', chunk => data += chunk\);
  res.on\(''end'', \(\) => {
    const tabs = JSON.parse\(data\);
    const tab = tabs.find\(t => t.url.includes\(''localhost:3000''\)\);
    if \(tab\) {
      const WebSocket = require\(''ws''\);
      const ws = new WebSocket\(tab.webSocketDebuggerUrl\);
      ws.on\(''open'', \(\) => {
        // Click at 10K card position \(approximately 780, 420\)
        ws.send\(JSON.stringify\({id: 1, method: ''Input.dispatchMouseEvent'', params: {type: ''mousePressed'', x: 780, y: 420, button: ''left'', clickCount: 1}}\)\);
        setTimeout\(\(\) => {
          ws.send\(JSON.stringify\({id: 2, method: ''Input.dispatchMouseEvent'', params: {type: ''mouseReleased'', x: 780, y: 420, button: ''left'', clickCount: 1}}\)\);
          setTimeout\(\(\) => { console.log\(''Clicked 10K''\); ws.close\(\); process.exit\(0\); }, 100\);
        }, 100\);
      }\);
    }
  }\);
}\);
")

